INSERT INTO blog_posts (post_author,post_date,post_date_gmt,post_content,post_title,post_excerpt,post_status,comment_status,ping_status,post_password,post_name,to_ping,pinged,post_modified,post_modified_gmt,post_content_filtered,post_parent,guid,menu_order,post_type,post_mime_type,comment_count,post_description,lang) VALUES (1,'2021-08-21 16:47:13','2021-08-21 16:47:13','<!-- wp:paragraph -->
